import React, {useEffect, useState} from 'react';
import './App.css'
import axios from "axios";

function App() {
    const [todos, setTodos] = useState();
    useEffect(()=> {
        const getUser = async() => {
            const response = await axios.get('http://localhost:3100/todos')

            setTodos(response.data)
        }
        getUser();
        console.log(todos)
    })
    return (
        <div className="container">
            {todos.map((post) => (
                <div className="card">
                    <h2>{post.title}</h2>
                    <p>{post.description}</p>
                    <div className="button-container">
                        <a href="#" className="btn btn-primary">Completed</a>
                        <a href="#" className="btn btn-secondary">Edit</a>
                        <a href="#" className="btn btn-success" onClick={(e) => this.deleteRow(post.id, e)}>Delete</a>
                    </div>
                </div>))}
        </div>

    );
}

export default App;
