import React, {useEffect, useState} from 'react';
import './App.css'
import axios from "axios";


function App() {
    const [todos, setTodos] =  useState([]);


    const fetchData = async () => {
        const response = await axios.get('http://localhost:3100/todos')
        setTodos(response.data.todos)
    }
    fetchData()

    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const postData = () => {
        axios.post(`http://localhost:3100/todos/`, {
            title: title,
            description: description
        })
        }
    return (<div>
        <div className="input-container">
            <input type="text" id="input-field" placeholder="Write ur todo title here" onChange={(e) => setTitle(e.target.value)}></input>
            <input type="text" id="input-field" placeholder="Write ur todo description here" onChange={(e) => setDescription(e.target.value)}></input>
            <button type="button" id="submit-btn" onClick={postData}>Add</button>
            {todos.map((post) => (
                <div className="card">
                    <h2>{post.title}</h2>
                    <p>{post.description}</p>
                    <div className="button-container">
                        <a href="#" className="btn btn-primary">Completed</a>
                        <a href="#" className="btn btn-secondary">Edit</a>
                        <a href="#" className="btn btn-success" onClick={(e) => this.deleteRow(post.id, e)}>Delete</a>
                    </div>
                </div>))}
        </div>
        </div>

    );
}

export default App;
