import React, {useEffect, useState} from 'react';
import './App.css'
import axios from "axios";
import {Link} from "@nextui-org/react";
import { Button, Modal, ModalTitle } from 'react-bootstrap'


function App(message) {
    const [todos, setTodos] =  useState([]);
    //edit
    const [ViewEdit, SetEditShow] = useState(false)
    const handleEditShow = () => { SetEditShow(true) }
    const handleEditClose = () => { SetEditShow(false) }
    const [RowData, SetRowData] = useState([])
    const [id,setId] = useState("");

    const handleEdit = () =>{
        const url = `https://localhost:3100/todos/${id}`
        const Credentials = { title, description }
        axios.patch(url, Credentials)
            .then(res => {
                const result = res.data.todos;
                const { status, message } = result;
                if (status !== 'SUCCESS') {
                    alert(message, status)
                }
                else {
                    alert(message)
                    window.location.reload()
                }
            })
            .catch(err => {
                console.log(err)
            })
    }

    const fetchData = async () => {
        try {
            const res = await axios.get('http://localhost:3100/todos')
            if (res.status === 200) {
                setTodos(res.data.todos)
                console.log('good!')
            }
        } catch (e) {
            console.log(e)
    }
        const res = await axios.get('http://localhost:3100/todos')
        setTodos(res.data.todos)
        console.log('good!')
    }
    useEffect(() => {
        fetchData();
    }, [])

    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const postData = async () => {
        try {
            await axios.post(`http://localhost:3100/todos/`, {
                title: title,
                description: description
            })
                .then(fetchData())
        } catch (e) {
            console.log(e)
        }
        }
    const deleteAll = async () => {
        try {
            await axios.delete(`http://localhost:3100/todos/`)
                .then(fetchData())
        } catch (e) {
            console.log(e)
        }

    }
    const deletePost = async (id) => {
        try {
            await axios.delete('http://localhost:3100/todos/' + id)
                .then(fetchData())
        } catch (e) {
            console.log(e)
        }
    }

    return (<div>
        <div>

            <div className="input-container">
                <input type="text" id="input-field" placeholder="Write ur todo title here" onChange={(e) => setTitle(e.target.value)}></input>
                <input type="text" id="input-field" placeholder="Write ur todo description here" onChange={(e) => setDescription(e.target.value)}></input>
                <button type="button" id="submit-btn" onClick={postData}>Add</button></div>

                <center><a href="#" className="btn btn-success" onClick={deleteAll}>Delete all</a></center>
            <center><a href="#" className="btn btn-success" onClick={fetchData}>update</a></center>
                {
                    todos.map((post) => (
                    <div key={post.id} className="card">
                        <h2>{post.title}</h2>
                        {post.description}
                        <div className="button-container">
                            <a href="#" className="btn btn-secondary" onClick={()=> {handleEditShow(SetRowData(post),setId(post.id))}}>Edit</a>
                            <a href="#" className="btn btn-success" onClick={() => deletePost(post.id)}>Delete</a>
                        </div>
                    </div>))}

        </div>
    <div className='model-box-view'>
        <Modal
            show={ViewEdit}
            onHide={handleEditClose}
            backdrop="static"
            keyboard={false}
        >
            <Modal.Header closeButton>
                <Modal.Title>Edit Employee</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div>
                    <div className='form-group'>
                        <label>Name</label>
                        <input type="text" className='form-control' onChange={(e) => setTitle(e.target.value)} placeholder="Please enter title" defaultValue={RowData.title}/>
                    </div>
                    <div className='form-group mt-3'>
                        <label>Email</label>
                        <input type="email" className='form-control' onChange={(e) => setDescription(e.target.value)} placeholder="Please enter description" defaultValue={RowData.description} />
                    </div>
                    <Button type='submit' className='btn btn-warning mt-4' onClick={handleEdit}>Edit Employee</Button>
                </div>
            </Modal.Body>
            <Modal.Footer>
                <Button variant='secondary' onClick={handleEditClose}>Close</Button>
            </Modal.Footer>
        </Modal>
    </div>
</div>
    )


}

export default App;
