import React from 'react';

import axios from 'axios';
import "./App.css";

export default class PostList extends React.Component {
    state = {
        posts: []
    }

    componentDidMount() {
        axios.get(`https://jsonplaceholder.typicode.com/posts`)
            .then(res => {
                const posts = res.data;
                this.setState({ posts });
            })
    }

    deleteRow(id, e){
        axios.delete(`https://jsonplaceholder.typicode.com/posts/${id}`)
            .then(res => {
                console.log(res);
                console.log(res.data);

                const posts = this.state.posts.filter(item => item.id !== id);
                this.setState({ posts });
            })

    }

    render() {
        return (
            <div id="DIV_1">
                <div id="DIV_2">
                    <img src="https://designmodo.github.io/Flat-UI/images/icons/svg/compas.svg" alt="Compas" id="IMG_3" />
                    <h3 id="H3_4">
                        Web Hosting
                    </h3>
                    <p id="P_5">
                        Put some impressive text here for visitors of your site.
                    </p> <a href="#" id="A_6">Learn More</a>
                </div>
            </div>
        )
    }
}
