import React, {useEffect, useState} from 'react';
import './App.css'
import axios from "axios";
import {Link} from "@nextui-org/react";


function App() {
    const [todos, setTodos] =  useState([]);


    const fetchData = async () => {
        const response = await axios.get('http://localhost:3100/todos')
        setTodos(response.data.todos)
    }
    fetchData()

    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const postData = async () => {
        await axios.post(`http://localhost:3100/todos/`, {
            title: title,
            description: description
        })
        }
    const deleteAll = async () => {
        await axios.delete(`http://localhost:3100/todos/`)
    }
    return (<div>
        <p>
            <div className="input-container">
                <input type="text" id="input-field" placeholder="Write ur todo title here" onChange={(e) => setTitle(e.target.value)}></input>
                <input type="text" id="input-field" placeholder="Write ur todo description here" onChange={(e) => setDescription(e.target.value)}></input>
                <button type="button" id="submit-btn" onClick={postData}>Add</button></div>
            <p>
                <center><a href="#" className="btn btn-success" onClick={deleteAll}>Delete all</a></center>
            </p>
        </p>
            <p>
                {
                    todos.map((post, index) => (
                    <div className="card">
                        <h2>{post.title}</h2>
                        <p>{post.description}</p>
                        <div className="button-container">
                            <a href="#" className="btn btn-primary">Completed</a>
                            <Link to='/update'><a href="#" className="btn btn-secondary">Edit</a></Link>
                            <a href="#" className="btn btn-success" onClick={(e) => this.deleteRow(post.id, e)}>Delete</a>
                        </div>
                    </div>))}
            </p>

        </div>

    );
}

export default App;
