import React from 'react';
import axios from 'axios';
import "./App.css";

export default class PostList extends React.Component {
    state = {
        posts: []
    }

    componentDidMount() {
        axios.get(`http://localhost:3100/todos`)
            .then(res => {
                const posts = res.data.todos;
                this.setState({ posts });
                console.log(posts)
            })
    }

    deleteRow(id, e){
        axios.delete(`http://localhost:3100/todos/${id}`)
            .then(res => {
                const posts = this.state.posts.filter(item => item.id !== id);
                this.setState({ posts })
            })

    }

    render() {
        return (
            <div className="container">
                <div className="input-container">
                    <input type="text" id="input-field" placeholder="Type something here"></input>
                        <button type="button" id="submit-btn">Submit</button>
                </div>
                <div className="card-container">
                    <div className="card">
                        <h3>Button 1</h3>
                        <button type="button">Click Me</button>
                    </div>
                    <div className="card">
                        <h3>Button 2</h3>
                        <button type="button">Click Me</button>
                    </div>
                    <div className="card">
                        <h3>Button 3</h3>
                        <button type="button">Click Me</button>
                    </div>
                    <div className="card">
                        <h3>Button 4</h3>
                        <button type="button">Click Me</button>
                    </div>
                </div>
            </div>
    )
    }
}
