import React from 'react';
import axios from 'axios';
import "./App.css";
import { Link } from "react-router-dom"

export default class PostList extends React.Component {
    state = {
        posts: []
    }

    componentDidMount() {
        axios.get(`http://localhost:3100/todos`)
            .then(res => {
                const posts = res.data.todos;
                this.setState({ posts });
                console.log(posts)
            })
    }

    deleteRow(id, e){
        axios.delete(`http://localhost:3100/todos/${id}`)
            .then(res => {
                const posts = this.state.posts.filter(item => item.id !== id);
                this.setState({ posts })
            })

    }

    render() {
        return (
            <link href="https://fonts.googleapis.com/css?family=Lato" rel="stylesheet">
                <div className="content">
                    <div className="content__container">
                        <p className="content__container__text">
                            Hello
                        </p>

                        <ul className="content__container__list">
                            <li className="content__container__list__item">world !</li>
                            <li className="content__container__list__item">bob !</li>
                            <li className="content__container__list__item">users !</li>
                            <li className="content__container__list__item">everybody !</li>
                        </ul>
                    </div>
                </div>
            <div className="container">
                <a>TODO-LIST app</a>
                <div className="input-container">
                    <input type="text" id="input-field" placeholder="Write ur todo here"></input>
                    <button type="button" id="submit-btn">Add</button>
                </div>

                    {this.state.posts.map((post) => (
                        <div className="card">
                            <h1>{post.title}</h1>
                            <p>{post.description}</p>
                            <div className="button-container">
                                <a href="#" className="btn btn-primary">Completed</a>
                                <a href="#" className="btn btn-secondary">Edit</a>
                                <a href="#" className="btn btn-success" onClick={(e) => this.deleteRow(post.id, e)}>Delete</a>
                            </div>
                        </div>))}
                </div>
            </link>

        )
    }
                }
