    props.onColumnResizeEnd && isResizing.current && props.onColumnResizeEnd(affectedColumnWidthsRef.current);
    setCurrentlyResizingColumn(null);
    isResizing.current = false;
    affectedColumnWidthsRef.current = [];

    let widths = new Map<Key, number>(columnWidthsRef.current);
    setColumnWidthsForRef(widths);
  }

  function resizeColumn(column: GridNode<T>, newWidth: number) : AffectedColumnWidths {
    let boundedWidth =  Math.max(
      getMinWidth(column.props.minWidth, tableWidth.current),
      Math.min(Math.floor(newWidth), getMaxWidth(column.props.maxWidth, tableWidth.current)));

    // copy the columnWidths map and set the new width for the column being resized
    let widths = new Map<Key, number>(columnWidthsRef.current);
    widths.set(column.key, boundedWidth);

    // keep track of all columns that have been sized
    resizedColumnsRef.current.add(column.key);
    setResizedColumns(resizedColumnsRef.current);

    // get the columns affected by resize and remaining space
    const resizeIndex = columnsRef.current.findIndex(col => col.key === column.key);
    let affectedColumns = columnsRef.current.slice(resizeIndex + 1);

    // we only care about the columns that CAN be resized, we ignore static columns.
    let {dynamicColumns} = getStaticAndDynamicColumns(affectedColumns);

    // available space for affected columns
    let availableSpace = columnsRef.current.reduce((acc, column, index) => {
      if (index <= resizeIndex || isStatic(getResolvedColumnWidth(column))) {
        return acc - widths.get(column.key);
      }
      return acc;
    }, tableWidth.current);

    // merge the unaffected column widths and the recalculated column widths
    let recalculatedColumnWidths = buildColumnWidths(dynamicColumns, availableSpace);
    widths = new Map<Key, number>([...widths, ...recalculatedColumnWidths]);

    setColumnWidthsForRef(widths);

    /*
     when getting recalculated columns above, the column being resized is not considered "recalculated"
     so we need to add it to the list of affected columns
    */
    let allAffectedColumns = ([[column.key, boundedWidth], ...recalculatedColumnWidths] as [Key, number][]).map(([key, width]) => ({key, width}));
    return allAffectedColumns;
  }

  // This function is regenerated whenever columnWidthsRef.current changes in order to get the new correct ref value.
  let getColumnWidth = useCallback((key: Key): number => columnWidthsRef.current.get(key) ?? 0, [columnWidthsRef.current]);

  let getColumnMinWidth = useCallback((key: Key) => {
    const columnIndex = columns.findIndex(col => col.key === key);
    if (columnIndex === -1) {
      return;
    }
    return getMinWidth(columns[columnIndex].props.minWidth, tableWidth.current);
  }, [columns]);

  let getColumnMaxWidth = useCallback((key: Key) => {
    const columnIndex = columns.findIndex(col => col.key === key);
    if (columnIndex === -1) {
      return;
    }
    return getMaxWidth(columns[columnIndex].props.maxWidth, tableWidth.current);
  }, [columns]);

  return {
    columnWidths: columnWidthsRef,
    setTableWidth,
    onColumnResize,
    onColumnResizeStart,
    onColumnResizeEnd,
    getColumnWidth,
    getColumnMinWidth,
    getColumnMaxWidth,
    currentlyResizingColumn
  };
}
import {GridNode} from '@react-types/grid';
import {Key} from 'react';

type mappedColumn<T> = GridNode<T> & {
    index: number,
    delta: number,
    calculatedWidth?: number
};

export function getContentWidth(widths: Map<Key, number>): number {
  return Array.from(widths).map(e => e[1]).reduce((acc, cur) => acc + cur, 0);
}

// numbers and percents are considered static. *fr units or a lack of units are considered dynamic.
export function isStatic(width: number | string): boolean {
  return width != null && (!isNaN(width as number) || (String(width)).match(/^(\d+)(?=%$)/) !== null);
}

function parseFractionalUnit(width: string): number {
  if (!width) {
    return 1;
  }
  let match = width.match(/^(\d+)(?=fr$)/);
      // if width is the incorrect format, just deafult it to a 1fr
  if (!match) {
    console.warn(`width: ${width} is not a supported format, width should be a number (ex. 150), percentage (ex. '50%') or fr unit (ex. '2fr')`,
        'defaulting to \'1fr\'');
    return 1;
  }
  return parseInt(match[0], 10);
}

export function parseStaticWidth(width: number | string, tableWidth: number): number {
  if (typeof width === 'string') {
    let match = width.match(/^(\d+)(?=%$)/);
    if (!match) {
      throw new Error('Only percentages or numbers are supported for static column widths');
    }
    return tableWidth * (parseInt(match[0], 10) / 100);
  }
  return width;
}


export function getMaxWidth(maxWidth: number | string, tableWidth: number): number {
  return maxWidth != null
        ? parseStaticWidth(maxWidth, tableWidth)
        : Number.MAX_SAFE_INTEGER;
}

export function getMinWidth(minWidth: number | string, tableWidth: number): number {
  return minWidth != null
      ? parseStaticWidth(minWidth, tableWidth)
      : 75;
}

function mapDynamicColumns<T>(dynamicColumns: GridNode<T>[], availableSpace: number, tableWidth: number): mappedColumn<T>[] {
  let fractions = dynamicColumns.reduce(
        (sum, column) => sum + parseFractionalUnit(column.props.defaultWidth),
        0
      );

  let columns = dynamicColumns.map((column, index) => {
    const targetWidth =
          (parseFractionalUnit(column.props.defaultWidth) * availableSpace) / fractions;
    const delta = Math.max(
      getMinWidth(column.props.minWidth, tableWidth) - targetWidth,
      targetWidth - getMaxWidth(column.props.maxWidth, tableWidth)
    );

    return {
      ...column,
      index,
      delta
    };
  });

  return columns;
}

function findDynamicColumnWidths<T>(dynamicColumns: mappedColumn<T>[], availableSpace: number, tableWidth: number): mappedColumn<T>[] {
  let fractions = dynamicColumns.reduce(
    (sum, col) => sum + parseFractionalUnit(col.props.defaultWidth),
    0
  );

  const columns = dynamicColumns.map((column) => {
    const targetWidth =
      (parseFractionalUnit(column.props.defaultWidth) * availableSpace) / fractions;
    let width = Math.max(
      getMinWidth(column.props.minWidth, tableWidth),
      Math.min(Math.floor(targetWidth), getMaxWidth(column.props.maxWidth, tableWidth))
    );
    column.calculatedWidth = width;
    availableSpace -= width;
    fractions -= parseFractionalUnit(column.props.defaultWidth);
    return column;
  });

  return columns;
}

export function getDynamicColumnWidths<T>(dynamicColumns: GridNode<T>[], availableSpace: number, tableWidth: number) {
  let columns = mapDynamicColumns(dynamicColumns, availableSpace, tableWidth);

  columns.sort((a, b) => b.delta - a.delta);
  columns = findDynamicColumnWidths(columns, availableSpace, tableWidth);
  columns.sort((a, b) => a.index - b.index);

  return columns;
}
