import React from 'react';
import axios from 'axios';
import "./App.css";

export default class PostList extends React.Component {
    state = {
        posts: []
    }

    componentDidMount() {
        axios.get(`http://localhost:3100/todos`)
            .then(res => {
                const posts = res.data.todos;
                this.setState({ posts });
                console.log(posts)
            })
    }

    deleteRow(id, e){
        axios.delete(`http://localhost:3100/todos/${id}`)
            .then(res => {
                const posts = this.state.posts.filter(item => item.id !== id);
                this.setState({ posts })
            })

    }

    render() {
        return (
            <div className="container">
                <h1>TODO-LIST app</h1>
                <div className="input-container">
                    <input type="text" id="input-field" placeholder="Write ur todo title here" onChange={(e) => setTitle(e.target.value)}></input>
                    <input type="text" id="input-field" placeholder="Write ur todo description here" onChange={(e) => setDescription(e.target.value)}></input>
                    <button type="button" id="submit-btn" onClick={postData}>Add</button>
                </div>
                    {this.state.posts.map((post) => (
                        <div className="card">
                            <h2>{post.title}</h2>
                            <p>{post.description}</p>
                            <div className="button-container">
                                <a href="#" className="btn btn-primary">Completed</a>
                                <a href="#" className="btn btn-secondary">Edit</a>
                                <a href="#" className="btn btn-success" onClick={(e) => this.deleteRow(post.id, e)}>Delete</a>
                            </div>
                        </div>))}
                </div>

        )
    }
}
