import logo from './logo.svg';
import {useEffect, useState} from "react";
import './App.css';
import Axios from 'axios';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

function App() {
    const [data, setData] = useState([])

    useEffect(() => {
        Axios.get('https://jsonplaceholder.typicode.com/todos')
            .then(res => {
                console.log(res.data)
                setData(res.data)
            })
            .catch(err => console.log(err))
    }, [])

    function handleRemove(id) {
        const newList = data.filter((data) => data.id !== id);

        setData(newList);
    }

    const array = data.map((data, index) => {
        return (
            <div className="card">
                <div className="header">
    <span className="icon">
      <svg fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
        <path clip-rule="evenodd"
              d="M18 3a1 1 0 00-1.447-.894L8.763 6H5a3 3 0 000 6h.28l1.771 5.316A1 1 0 008 18h1a1 1 0 001-1v-4.382l6.553 3.276A1 1 0 0018 15V3z"
              fill-rule="evenodd"></path>
      </svg>
    </span>
                    <p className="alert">ToDo number {data.id}</p>
                </div>

                <p className="message">
                    {data.title}
                </p>

                <p className="message-status">
                    isCompleted: {String(data.completed)}
                </p>

                <div className="actions">
                    <a className="read" href="">
                        Completed
                    </a>

                    <a className="mark-as-read" href="" role="button" onClick={handleRemove(data.id)}>
                        Delete
                    </a>
                </div>
            </div>


        )
    })
  return (
    <div className="App">
        {array}
    </div>


  );
}

export default App;
