import React from 'react';
import axios from 'axios';
import "./App.css";

export default class PostList extends React.Component {
    state = {
        posts: []
    }

    componentDidMount() {
        axios.get(`http://localhost:3100/todos`)
            .then(res => {
                const posts = res.data.todos;
                this.setState({ posts });
                console.log(posts)
            })
    }

    deleteRow(id, e){
        axios.delete(`http://localhost:3100/todos/${id}`)
            .then(res => {
                const posts = this.state.posts.filter(item => item.id !== id);
                this.setState({ posts })
            })

    }

    render() {
        return (
            <div className="container">
                <div className="card">
                    {this.state.posts.map((post) => (
                        <div className="card">
                            <h1>{post.title}</h1>
                            <p>{post.description}</p>
                            <div className="button-container">
                                <a href="#" className="btn btn-primary">Completed</a>
                                <a href="#" className="btn btn-secondary">Edit</a>
                                <a className="btn btn-success" onClick={(e) => this.deleteRow(post.id, e)}>Delete</a>
                            </div>
                        </div>))}
                </div>
            </div>
        )
    }
}
