import React, { useState } from 'react';
import axios from 'axios';
export default function Create() {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const postData = () => {
        axios.post(`http://localhost:3100/todos/`, {
            title: title,
            description: description
        })
    }
    return (
        <div className="container">
            <h1>TODO-LIST app</h1>
            <div className="input-container">
                <input type="text" id="input-field" defaultValue="Hello!" placeholder="Write ur todo title here" onChange={(e) => setTitle(e.target.value)}></input>
                <input type="text" id="input-field" placeholder="Write ur todo description here" onChange={(e) => setDescription(e.target.value)}></input>
                <button type="button" id="submit-btn" onClick={postData}>Add</button>
            </div>
        </div>
    )
}
